module.exports = {
			dataObject:function(objectType) {
				var jsonResult = '';

				var api = '';

				var foldersAPI = 'https://custom.pocketreporter.co.za/wp-json/wp/v2/election_folders';
				var resourceAPI = 'https://custom.pocketreporter.co.za/wp-json/wp/v2/election_information';
		  	var questionAPI = 'https://custom.pocketreporter.co.za/wp-json/wp/v2/election_questions/';

				switch(objectType){
					case 'folders':
					  api = foldersAPI;
						break;
					case 'resources':
					 api = resourceAPI;
					 break;
					case 'questions':
					 api = questionAPI;
					 break;
				}

				$.ajax({
        type: 'GET',
        url:api,
				async:false,
        success: function (response) {
            jsonResult = response;
        },
        error: function (xhr, ajaxOptions, thrownError) {
            throw(xhr.responseText);
        },
        beforeSend: function(){
        },
        complete: function() {
        }
    });

		return jsonResult;
	}
};
